import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Popup, Polyline, Circle, useMap } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
// import './App.css'; // Removed to prevent conflicts with new horizontal slider

// Fix default marker paths
delete (L.Icon.Default.prototype as any)._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

// Function to calculate bearing between two points
const calculateBearing = (lat1: number, lon1: number, lat2: number, lon2: number): number => {
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const lat1Rad = lat1 * Math.PI / 180;
  const lat2Rad = lat2 * Math.PI / 180;
  
  const y = Math.sin(dLon) * Math.cos(lat2Rad);
  const x = Math.cos(lat1Rad) * Math.sin(lat2Rad) - Math.sin(lat1Rad) * Math.cos(lat2Rad) * Math.cos(dLon);
  
  let bearing = Math.atan2(y, x) * 180 / Math.PI;
  bearing = (bearing + 360) % 360; // Normalize to 0-360
  
  // Adjust for airplane emoji orientation (airplane emoji points northeast by default)
  // Subtract 45 degrees to align with the route properly
  bearing = bearing - 45;
  
  return bearing;
};

// Function to create rotated airplane icon
const createAirplaneIcon = (rotation: number) => {
  return L.divIcon({
    className: 'airplane-icon',
    html: `<div style="
      transform: rotate(${rotation}deg); 
      font-size: 24px; 
      display: flex; 
      align-items: center; 
      justify-content: center; 
      width: 100%; 
      height: 100%;
      transform-origin: center center;
    ">✈️</div>`,
    iconSize: [30, 30],
    iconAnchor: [15, 15],
  });
};



interface Waypoint {
  lat: number;
  lon: number;
  time: string;
}

interface SunPosition {
  lat: number;
  lon: number;
  time: string;
  azimuth: number;
  elevation: number;
}

interface FlightData {
  path: Waypoint[];
  sunPositions: SunPosition[];
  recommendation: string;
}

// Flight Progress Slider Component
const FlightProgressSlider: React.FC<{
  flightData: FlightData | null;
  flightProgress: number;
  onProgressChange: (progress: number) => void;
}> = ({ flightData, flightProgress, onProgressChange }) => {
  const [isDragging, setIsDragging] = useState(false);
  const sliderRef = useRef<HTMLDivElement>(null);

  if (!flightData || flightData.path.length === 0) {
    console.log('FlightProgressSlider: No flight data or empty path', { flightData, pathLength: flightData?.path?.length });
    return (
      <div className="flight-progress-container">
        <h4>Flight Timeline</h4>
        <p className="progress-instructions">Loading flight data...</p>
      </div>
    );
  }

  const handleMouseDown = (e: React.MouseEvent) => {
    e.preventDefault();
    setIsDragging(true);
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
  };

  const handleMouseMove = (e: MouseEvent) => {
    if (!isDragging || !sliderRef.current) return;
    
    const rect = sliderRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const percentage = Math.max(0, Math.min(100, (x / rect.width) * 100));
    
    onProgressChange(percentage);
  };

  const handleMouseUp = () => {
    setIsDragging(false);
    document.removeEventListener('mousemove', handleMouseMove);
    document.removeEventListener('mouseup', handleMouseUp);
  };

  const handleClick = (e: React.MouseEvent) => {
    if (!sliderRef.current) return;
    
    const rect = sliderRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const percentage = Math.max(0, Math.min(100, (x / rect.width) * 100));
    
    onProgressChange(percentage);
  };

  return (
    <div className="flight-progress-container">
      <h4>Flight Timeline</h4>
      <p className="progress-instructions">Slide to preview sun position during your flight</p>
      
      <div className="progress-slider-container">
        <div className="progress-labels">
          <span className="progress-label">
            <span className="progress-icon">🛫</span>
            Departure
          </span>
          <span className="progress-label">
            <span className="progress-icon">🛬</span>
            Arrival
          </span>
        </div>
        
        <div 
          ref={sliderRef}
          className="horizontal-slider-container"
          onClick={handleClick}
          onMouseDown={handleMouseDown}
        >
          <div className="horizontal-slider-track">
            <div 
              className="horizontal-progress-bar" 
              style={{ width: `${flightProgress}%` }}
            ></div>
            <div 
              className="horizontal-slider-handle"
              style={{ left: `${flightProgress}%` }}
              onMouseDown={handleMouseDown}
            ></div>
          </div>
        </div>
        
        <div className="progress-display">
          Flight Progress: {Math.round(flightProgress)}%
        </div>
      </div>
    </div>
  );
};

// Interactive Airplane Component
const InteractiveAirplane: React.FC<{
  flightData: FlightData | null;
  flightProgress: number;
  onPositionChange: (position: { lat: number; lon: number; sunPosition: SunPosition | null }) => void;
}> = ({ flightData, flightProgress, onPositionChange }) => {
  const map = useMap();
  const airplaneRef = useRef<L.Marker | null>(null);

  useEffect(() => {
    if (!flightData || flightData.path.length === 0) return;

    // Calculate waypoint index based on flight progress (0-100%)
    const waypointIndex = flightProgress >= 100 
      ? flightData.path.length - 1 
      : Math.floor((flightProgress / 100) * (flightData.path.length - 1));
    const currentWaypoint = flightData.path[waypointIndex];

    // Find corresponding sun position (for data display only, no marker)
    const currentSunPosition = flightData.sunPositions[waypointIndex] || null;

    onPositionChange({
      lat: currentWaypoint.lat,
      lon: currentWaypoint.lon,
      sunPosition: currentSunPosition
    });

    // Update map view to follow airplane with smooth animation
    map.setView([currentWaypoint.lat, currentWaypoint.lon], map.getZoom(), { 
      animate: true,
      duration: 0.1 // Very short animation for smooth following
    });

  }, [flightData, flightProgress, onPositionChange]);





  // Get current airplane position and calculate rotation based on flight progress
  const getCurrentAirplaneData = (): { position: [number, number] | null; rotation: number } => {
    if (!flightData || flightData.path.length === 0) return { position: null, rotation: 0 };
    
    // Calculate waypoint index based on flight progress (0-100%)
    const waypointIndex = flightProgress >= 100 
      ? flightData.path.length - 1 
      : Math.floor((flightProgress / 100) * (flightData.path.length - 1));
    const currentWaypoint = flightData.path[waypointIndex];
    
    // Calculate bearing for airplane rotation using overall flight direction
    let rotation = 0;
    if (flightData.path.length > 1) {
      // Always use the overall flight direction from start to end for consistency
      const startPoint = flightData.path[0];
      const endPoint = flightData.path[flightData.path.length - 1];
      rotation = calculateBearing(
        startPoint.lat, startPoint.lon,
        endPoint.lat, endPoint.lon
      );
    }
    
    return { 
      position: [currentWaypoint.lat, currentWaypoint.lon],
      rotation: rotation
    };
  };

  const { position: currentPosition, rotation: airplaneRotation } = getCurrentAirplaneData();

  return (
    <>
      {currentPosition && (
                 <Marker
           ref={airplaneRef}
           position={currentPosition}
           icon={createAirplaneIcon(airplaneRotation)}
           draggable={false}
           interactive={false}
           bubblingMouseEvents={false}
           eventHandlers={{
             add: (e) => {
               const marker = e.target;
               if (marker._icon) {
                 marker._icon.style.pointerEvents = 'none';
                 marker._icon.style.userSelect = 'none';
                 marker._icon.style.webkitUserDrag = 'none';
                 marker._icon.draggable = false;
               }
             },
             dragstart: (e) => { e.preventDefault(); return false; },
             drag: (e) => { e.preventDefault(); return false; },
             dragend: (e) => { e.preventDefault(); return false; },
             mousedown: (e) => { e.originalEvent?.preventDefault(); return false; },
             mouseup: (e) => { e.originalEvent?.preventDefault(); return false; },
             mousemove: (e) => { e.originalEvent?.preventDefault(); return false; }
           }}
         >
           <Popup>
             Airplane Position<br />
             Use the timeline slider to move me!
           </Popup>
         </Marker>
      )}
    </>
  );
};

function App() {
  const [fromAirport, setFromAirport] = useState('DEL');
  const [toAirport, setToAirport] = useState('JAI');
  const [departureTime, setDepartureTime] = useState('2025-08-01T18:00');
  const [flightData, setFlightData] = useState<FlightData | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [flightProgress, setFlightProgress] = useState<number>(0);
  const [currentPosition, setCurrentPosition] = useState<{ lat: number; lon: number } | null>(null);
  const [currentSunPosition, setCurrentSunPosition] = useState<SunPosition | null>(null);

  // Sun Preferences State
  const [wantSunrise, setWantSunrise] = useState(false);
  const [wantSunset, setWantSunset] = useState(false);
  const [sunrisePreference, setSunrisePreference] = useState<'sunrise' | 'sunset'>('sunrise');

  const [isDragging, setIsDragging] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    // Check if source and destination are the same
    if (fromAirport === toAirport) {
      setError('No flights available for the same source and destination airports. Please select different departure and arrival airports.');
      setLoading(false);
      return;
    }

    try {
      const response = await axios.get(`http://localhost:3001/api/route`, {
        params: {
          from: fromAirport,
          to: toAirport,
          depart: departureTime
        }
      });

      // Validate the response data
      if (!response.data || !response.data.path || response.data.path.length === 0) {
        setError('No flight path data available for this route. Please try a different route or time.');
        setLoading(false);
        return;
      }

      setFlightData(response.data);
      
      // Set initial flight progress to 0% (departure)
      setFlightProgress(0);
      
    } catch (err) {
      setError('Unable to fetch the data');
      console.error('Error fetching flight data:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleProgressChange = useCallback((newProgress: number) => {
    setFlightProgress(newProgress);
  }, []);

  const handleMouseDown = (e: React.MouseEvent) => {
    e.preventDefault();
    setIsDragging(true);
    
    // Immediately update position on mouse down for instant feedback
    if (flightData) {
      const sliderElement = e.currentTarget.closest('.horizontal-slider-container') as HTMLElement;
      if (sliderElement) {
        const rect = sliderElement.getBoundingClientRect();
        const x = e.clientX - rect.left;
        let percentage = (x / rect.width) * 100;
        
        // Make it easier to reach 100% - if within 5% of the end, snap to 100%
        if (percentage > 95) percentage = 100;
        if (percentage < 5) percentage = 0;
        
        percentage = Math.max(0, Math.min(100, percentage));
        
        // Immediately update the visual elements without waiting for React state
        const handle = document.querySelector('.horizontal-slider-handle') as HTMLElement;
        const progressBar = document.querySelector('.horizontal-progress-bar') as HTMLElement;
        const progressText = document.querySelector('.progress-display') as HTMLElement;
        
        if (handle) handle.style.left = `${percentage}%`;
        if (progressBar) progressBar.style.width = `${percentage}%`;
        if (progressText) progressText.textContent = `Flight Progress: ${Math.round(percentage)}%`;
        
        handleProgressChange(percentage);
      }
    }
    
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
  };

  const handleMouseMove = (e: MouseEvent) => {
    if (!isDragging || !flightData) return;
    
    const sliderElement = document.querySelector('.horizontal-slider-container') as HTMLElement;
    if (!sliderElement) return;
    
    const rect = sliderElement.getBoundingClientRect();
    const x = e.clientX - rect.left;
    let percentage = (x / rect.width) * 100;
    
    // Make it easier to reach 100% - if within 5% of the end, snap to 100%
    if (percentage > 95) percentage = 100;
    if (percentage < 5) percentage = 0;
    
    percentage = Math.max(0, Math.min(100, percentage));
    
    // Immediately update the visual elements without waiting for React state
    const handle = document.querySelector('.horizontal-slider-handle') as HTMLElement;
    const progressBar = document.querySelector('.horizontal-progress-bar') as HTMLElement;
    const progressText = document.querySelector('.progress-display') as HTMLElement;
    
    if (handle) handle.style.left = `${percentage}%`;
    if (progressBar) progressBar.style.width = `${percentage}%`;
    if (progressText) progressText.textContent = `Flight Progress: ${Math.round(percentage)}%`;
    
    // Update React state (this will update airplane position)
    handleProgressChange(percentage);
  };

  const handleMouseUp = () => {
    setIsDragging(false);
    document.removeEventListener('mousemove', handleMouseMove);
    document.removeEventListener('mouseup', handleMouseUp);
  };

  const handlePositionChange = (position: { lat: number; lon: number; sunPosition: SunPosition | null }) => {
    setCurrentPosition({ lat: position.lat, lon: position.lon });
    setCurrentSunPosition(position.sunPosition);
  };

  return (
    <div className="app">
      <div className="container">
        <div className="header">
          <h1>Flight Seat Recommender</h1>
          <p>Find the best window seat for sunrise/sunset views</p>
        </div>

        <div className="content">
          <div className="form-section">
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="fromAirport">From Airport:</label>
                <select
                  id="fromAirport"
                  value={fromAirport}
                  onChange={(e) => setFromAirport(e.target.value)}
                >
                  <option value="DEL">Delhi (DEL)</option>
                  <option value="JAI">Jaipur (JAI)</option>
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="toAirport">To Airport:</label>
                <select
                  id="toAirport"
                  value={toAirport}
                  onChange={(e) => setToAirport(e.target.value)}
                >
                  <option value="DEL">Delhi (DEL)</option>
                  <option value="JAI">Jaipur (JAI)</option>
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="departureTime">Departure Time:</label>
                <input
                  type="datetime-local"
                  id="departureTime"
                  value={departureTime}
                  onChange={(e) => setDepartureTime(e.target.value)}
                />
              </div>

              <button type="submit" className="btn" disabled={loading}>
                {loading ? 'Finding Seat...' : 'Find My Seat'}
              </button>
            </form>
          </div>

          {error && (
            <div className="error">
              {error}
            </div>
          )}

          {flightData && (
            <div className="results-section">
              <div className="recommendation">
                <h3>Recommended Seat Side</h3>
                <p>
                  {flightData.recommendation === 'left' ? '← LEFT SIDE' : 
                   flightData.recommendation === 'right' ? 'RIGHT SIDE →' : 
                   flightData.recommendation}
                </p>
              </div>

              <div className="results-container">
                <div className="map-section">
                                     <h3>Interactive Flight Route</h3>
                   <p className="map-instructions">
                     Use the timeline slider below to move the airplane along the route
                   </p>
                  
                  <div className="map-container">
                    <MapContainer
                      center={[28.5562, 77.1000]}
                      zoom={8}
                      style={{ height: '500px', width: '100%' }}
                    >
                      <TileLayer
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                      />

                      {/* Flight path */}
                      {flightData.path && flightData.path.length > 1 && (
                        <Polyline
                          positions={flightData.path.map(wp => [wp.lat, wp.lon])}
                          color="blue"
                          weight={3}
                        />
                      )}

                      {/* From airport marker */}
                      {flightData.path && flightData.path.length > 0 && (
                        <Marker
                          position={[flightData.path[0].lat, flightData.path[0].lon]}
                          icon={L.divIcon({
                            className: 'airport-marker',
                            html: '🛫',
                            iconSize: [30, 30],
                            iconAnchor: [15, 15],
                          })}
                        >
                          <Popup>Departure: {fromAirport}</Popup>
                        </Marker>
                      )}

                      {/* To airport marker */}
                      {flightData.path && flightData.path.length > 0 && (
                        <Marker
                          position={[flightData.path[flightData.path.length - 1].lat, flightData.path[flightData.path.length - 1].lon]}
                          icon={L.divIcon({
                            className: 'airport-marker',
                            html: '🛬',
                            iconSize: [30, 30],
                            iconAnchor: [15, 15],
                          })}
                        >
                          <Popup>Arrival: {toAirport}</Popup>
                        </Marker>
                      )}

                      



                      <InteractiveAirplane
                        flightData={flightData}
                        flightProgress={flightProgress}
                        onPositionChange={handlePositionChange}
                      />
                    </MapContainer>
                  </div>
                </div>

                <div className="flight-details">
                  {/* Flight Details Section */}
                  <h3>Flight Details</h3>
                  <div className="details-grid">
                    <div className="detail-item">
                      <strong>Route:</strong> {fromAirport} → {toAirport}
                    </div>
                    <div className="detail-item">
                      <strong>Departure:</strong> {new Date(departureTime).toLocaleString()}
                    </div>
                    <div className="detail-item">
                      <strong>Duration:</strong> ~{Math.round(flightData.path.length * 15)} minutes
                    </div>
                    <div className="detail-item">
                      <strong>Waypoints:</strong> {flightData.path.length}
                    </div>
                  </div>

                  {currentSunPosition && (
                    <div className="live-position">
                      <h3>Live Position</h3>
                      <div className="position-details">
                        <p><strong>Airplane:</strong> {currentPosition ? currentPosition.lat.toFixed(4) : '--'}, {currentPosition ? currentPosition.lon.toFixed(4) : '--'}</p>
                        <p><strong>Sun Azimuth:</strong> {currentSunPosition?.azimuth !== undefined ? currentSunPosition.azimuth.toFixed(1) : '--'}°</p>
                        <p><strong>Sun Elevation:</strong> {currentSunPosition?.elevation !== undefined ? currentSunPosition.elevation.toFixed(1) : '--'}°</p>
                        <p><strong>Time:</strong> {new Date(currentSunPosition.time).toLocaleTimeString()}</p>
                      </div>
                    </div>
                  )}

                  {/* Flight Timeline - RIGHT PANEL */}
                  <div className="flight-progress-container" style={{ backgroundColor: '#2c3e50', margin: '20px 0', padding: '20px', borderRadius: '8px' }}>
                    <h4 style={{ color: '#f39c12', margin: '0 0 10px 0' }}>Flight Timeline</h4>
                    <p style={{ color: '#ecf0f1', fontStyle: 'italic', marginBottom: '15px' }}>Slide to preview sun position during your flight</p>
                    
                    <div className="progress-slider-container">
                      <div className="progress-labels" style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '10px', color: '#ecf0f1' }}>
                        <span className="progress-label" style={{ display: 'flex', alignItems: 'center', gap: '5px' }}>
                          <span className="progress-icon">🛫</span>
                          Departure
                        </span>
                        <span className="progress-label" style={{ display: 'flex', alignItems: 'center', gap: '5px' }}>
                          <span className="progress-icon">🛬</span>
                          Arrival
                        </span>
                      </div>
                      
                      <div 
                        className="horizontal-slider-container"
                        style={{ 
                          position: 'relative', 
                          height: '40px', 
                          display: 'flex', 
                          alignItems: 'center', 
                          cursor: 'pointer', 
                          userSelect: 'none' 
                        }}
                        onClick={(e) => {
                          if (!flightData) return;
                          const rect = e.currentTarget.getBoundingClientRect();
                          const x = e.clientX - rect.left;
                          let percentage = (x / rect.width) * 100;
                          
                          // Make it easier to reach 100% - if within 5% of the end, snap to 100%
                          if (percentage > 95) percentage = 100;
                          if (percentage < 5) percentage = 0;
                          
                          percentage = Math.max(0, Math.min(100, percentage));
                          handleProgressChange(percentage);
                        }}
                      >
                        <div 
                          className="horizontal-slider-track"
                          style={{
                            width: '100%',
                            height: '8px',
                            background: '#34495e',
                            borderRadius: '4px',
                            position: 'relative'
                          }}
                        >
                          <div 
                            className="horizontal-progress-bar" 
                            style={{ 
                              height: '100%',
                              background: 'linear-gradient(135deg, #f39c12 0%, #e67e22 100%)',
                              borderRadius: '4px',
                              position: 'absolute',
                              top: '0',
                              left: '0',
                              width: `${flightProgress}%`
                            }}
                          />
                          <div 
                            className="horizontal-slider-handle"
                            style={{
                              position: 'absolute',
                              top: '50%',
                              width: '24px',
                              height: '24px',
                              background: '#f39c12',
                              border: '3px solid white',
                              borderRadius: '50%',
                              transform: 'translate(-50%, -50%)',
                              cursor: isDragging ? 'grabbing' : 'grab',
                              boxShadow: '0 3px 6px rgba(0, 0, 0, 0.3)',
                              left: `${flightProgress}%`
                            }}
                            onMouseDown={handleMouseDown}
                          />
                        </div>
                      </div>
                      
                      <div style={{ marginTop: '10px', textAlign: 'center', fontWeight: '600', color: '#ecf0f1' }}>
                        Flight Progress: {Math.round(flightProgress)}%
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
