import React, { useState, useRef, useEffect, useCallback } from 'react';
import './index.css';
import 'leaflet/dist/leaflet.css';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import L from 'leaflet';
import axios from 'axios';

// Fix for default markers
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

// Types
interface Waypoint {
  lat: number;
  lon: number;
}

interface SunPosition {
  azimuth: number;
  elevation: number;
}

interface FlightData {
  path: Waypoint[];
  sunPositions: SunPosition[];
  recommendation: string;
  distance?: number;
  flightTime?: number;
}

// Function to calculate bearing between two points
const calculateBearing = (lat1: number, lon1: number, lat2: number, lon2: number): number => {
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const lat1Rad = lat1 * Math.PI / 180;
  const lat2Rad = lat2 * Math.PI / 180;

  const y = Math.sin(dLon) * Math.cos(lat2Rad);
  const x = Math.cos(lat1Rad) * Math.sin(lat2Rad) - Math.sin(lat1Rad) * Math.cos(lat2Rad) * Math.cos(dLon);

  let bearing = Math.atan2(y, x) * 180 / Math.PI;
  bearing = (bearing + 360) % 360; // Normalize to 0-360

  // Adjust for airplane emoji orientation (airplane emoji points northeast by default)
  // Subtract 45 degrees to align with the route properly
  bearing = bearing - 45;

  return bearing;
};

// Function to create rotated airplane icon
const createAirplaneIcon = (rotation: number) => {
  return L.divIcon({
    className: 'airplane-icon',
    html: `<div style="
      transform: rotate(${rotation}deg); 
      font-size: 24px; 
      display: flex; 
      align-items: center; 
      justify-content: center; 
      width: 100%; 
      height: 100%;
      transform-origin: center center;
    ">‚úàÔ∏è</div>`,
    iconSize: [30, 30],
    iconAnchor: [15, 15],
  });
};

// InteractiveAirplane component
interface InteractiveAirplaneProps {
  flightData: FlightData;
  flightProgress: number;
  onPositionChange: (position: { lat: number; lon: number; sunPosition: SunPosition }) => void;
}

const InteractiveAirplane: React.FC<InteractiveAirplaneProps> = ({ 
  flightData, 
  flightProgress, 
  onPositionChange 
}) => {
  const airplaneRef = useRef<L.Marker>(null);

  const getCurrentAirplaneData = (): { position: [number, number] | null; rotation: number } => {
    if (!flightData || flightData.path.length === 0) return { position: null, rotation: 0 };

    const waypointIndex = flightProgress >= 100 
      ? flightData.path.length - 1 
      : Math.floor((flightProgress / 100) * (flightData.path.length - 1));
    const currentWaypoint = flightData.path[waypointIndex];

    let rotation = 0;
    if (flightData.path.length > 1) {
      // Always use the overall flight direction from start to end for consistency
      const startPoint = flightData.path[0];
      const endPoint = flightData.path[flightData.path.length - 1];
      rotation = calculateBearing(
        startPoint.lat, startPoint.lon,
        endPoint.lat, endPoint.lon
      );
    }

    return { 
      position: [currentWaypoint.lat, currentWaypoint.lon],
      rotation: rotation
    };
  };

  const { position: currentPosition, rotation: airplaneRotation } = getCurrentAirplaneData();

  useEffect(() => {
    if (!flightData || flightData.path.length === 0) return;

    const waypointIndex = flightProgress >= 100 
      ? flightData.path.length - 1 
      : Math.floor((flightProgress / 100) * (flightData.path.length - 1));
    const currentWaypoint = flightData.path[waypointIndex];
    const currentSunPosition = flightData.sunPositions[waypointIndex] || null;

    // Update position callback
    onPositionChange({
      lat: currentWaypoint.lat,
      lon: currentWaypoint.lon,
      sunPosition: currentSunPosition
    });

    // Update map view to follow airplane with smooth animation
    if (airplaneRef.current) {
      const leafletElement = airplaneRef.current as any;
      const map = leafletElement._map;
      if (map) {
        map.setView([currentWaypoint.lat, currentWaypoint.lon], map.getZoom(), { 
          animate: true,
          duration: 0.1 // Very short animation for smooth following
        });
      }
    }
  }, [flightData, flightProgress]); // Removed onPositionChange from dependencies

  if (!currentPosition) return null;

  return (
    <Marker
      ref={airplaneRef}
      position={currentPosition}
      icon={createAirplaneIcon(airplaneRotation)}
      draggable={false}
      interactive={false}
      bubblingMouseEvents={false}
      eventHandlers={{
        dragstart: (e) => { (e as any).preventDefault?.(); return false; },
        drag: (e) => { (e as any).preventDefault?.(); return false; },
        dragend: (e) => { (e as any).preventDefault?.(); return false; },
        mousedown: (e) => { e.originalEvent?.preventDefault(); return false; },
        mouseup: (e) => { e.originalEvent?.preventDefault(); return false; },
        mousemove: (e) => { e.originalEvent?.preventDefault(); return false; },
        add: () => {
          if (airplaneRef.current) {
            const element = airplaneRef.current.getElement();
            if (element) {
              element.style.pointerEvents = 'none';
              element.style.userSelect = 'none';
              element.style.touchAction = 'none';
              element.draggable = false;
            }
          }
        }
      }}
    >
      <Popup>
        Airplane Position<br />
        Use the timeline slider to move me!
      </Popup>
    </Marker>
  );
};

