import React, { useState, useRef, useEffect, useCallback } from 'react';
import './index.css';
import 'leaflet/dist/leaflet.css';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import L from 'leaflet';
import axios from 'axios';

// Fix for default markers
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

// Types
interface Waypoint {
  lat: number;
  lon: number;
}

interface SunPosition {
  azimuth: number;
  elevation: number;
}

interface FlightData {
  path: Waypoint[];
  sunPositions: SunPosition[];
  recommendation: string;
  distance?: number;
  flightTime?: number;
}

// Function to calculate bearing between two points
const calculateBearing = (lat1: number, lon1: number, lat2: number, lon2: number): number => {
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const lat1Rad = lat1 * Math.PI / 180;
  const lat2Rad = lat2 * Math.PI / 180;

  const y = Math.sin(dLon) * Math.cos(lat2Rad);
  const x = Math.cos(lat1Rad) * Math.sin(lat2Rad) - Math.sin(lat1Rad) * Math.cos(lat2Rad) * Math.cos(dLon);

  let bearing = Math.atan2(y, x) * 180 / Math.PI;
  bearing = (bearing + 360) % 360; // Normalize to 0-360

  // Adjust for airplane emoji orientation (airplane emoji points northeast by default)
  // Subtract 45 degrees to align with the route properly
  bearing = bearing - 45;

  return bearing;
};

// Function to create rotated airplane icon
const createAirplaneIcon = (rotation: number) => {
  return L.divIcon({
    className: 'airplane-icon',
    html: `<div style="
      transform: rotate(${rotation}deg); 
      font-size: 24px; 
      display: flex; 
      align-items: center; 
      justify-content: center; 
      width: 100%; 
      height: 100%;
      transform-origin: center center;
    ">✈️</div>`,
    iconSize: [30, 30],
    iconAnchor: [15, 15],
  });
};

// InteractiveAirplane component
interface InteractiveAirplaneProps {
  flightData: FlightData;
  flightProgress: number;
  onPositionChange: (position: { lat: number; lon: number; sunPosition: SunPosition }) => void;
}

const InteractiveAirplane: React.FC<InteractiveAirplaneProps> = ({ 
  flightData, 
  flightProgress, 
  onPositionChange 
}) => {
  const airplaneRef = useRef<L.Marker>(null);

  const getCurrentAirplaneData = (): { position: [number, number] | null; rotation: number } => {
    if (!flightData || flightData.path.length === 0) return { position: null, rotation: 0 };

    const waypointIndex = flightProgress >= 100 
      ? flightData.path.length - 1 
      : Math.floor((flightProgress / 100) * (flightData.path.length - 1));
    const currentWaypoint = flightData.path[waypointIndex];

    let rotation = 0;
    if (flightData.path.length > 1) {
      // Always use the overall flight direction from start to end for consistency
      const startPoint = flightData.path[0];
      const endPoint = flightData.path[flightData.path.length - 1];
      rotation = calculateBearing(
        startPoint.lat, startPoint.lon,
        endPoint.lat, endPoint.lon
      );
    }

    return { 
      position: [currentWaypoint.lat, currentWaypoint.lon],
      rotation: rotation
    };
  };

  const { position: currentPosition, rotation: airplaneRotation } = getCurrentAirplaneData();

  useEffect(() => {
    if (!flightData || flightData.path.length === 0) return;

    const waypointIndex = flightProgress >= 100 
      ? flightData.path.length - 1 
      : Math.floor((flightProgress / 100) * (flightData.path.length - 1));
    const currentWaypoint = flightData.path[waypointIndex];
    const currentSunPosition = flightData.sunPositions[waypointIndex] || null;

    // Update position callback
    onPositionChange({
      lat: currentWaypoint.lat,
      lon: currentWaypoint.lon,
      sunPosition: currentSunPosition
    });

    // Update map view to follow airplane with smooth animation
    if (airplaneRef.current) {
      const leafletElement = airplaneRef.current as any;
      const map = leafletElement._map;
      if (map) {
        map.setView([currentWaypoint.lat, currentWaypoint.lon], map.getZoom(), { 
          animate: true,
          duration: 0.1 // Very short animation for smooth following
        });
      }
    }
  }, [flightData, flightProgress,onPositionChange]); // Removed onPositionChange from dependencies

  if (!currentPosition) return null;

  return (
    <Marker
      ref={airplaneRef}
      position={currentPosition}
      icon={createAirplaneIcon(airplaneRotation)}
      draggable={false}
      interactive={false}
      bubblingMouseEvents={false}
      eventHandlers={{
        dragstart: (e) => { (e as any).preventDefault?.(); return false; },
        drag: (e) => { (e as any).preventDefault?.(); return false; },
        dragend: (e) => { (e as any).preventDefault?.(); return false; },
        mousedown: (e) => { e.originalEvent?.preventDefault(); return false; },
        mouseup: (e) => { e.originalEvent?.preventDefault(); return false; },
        mousemove: (e) => { e.originalEvent?.preventDefault(); return false; },
        add: () => {
          if (airplaneRef.current) {
            const element = airplaneRef.current.getElement();
            if (element) {
              element.style.pointerEvents = 'none';
              element.style.userSelect = 'none';
              element.style.touchAction = 'none';
              element.draggable = false;
            }
          }
        }
      }}
    >
      <Popup>
        Airplane Position<br />
        Use the timeline slider to move me!
      </Popup>
    </Marker>
  );
};

// Page enum for navigation
enum AppPage {
  SEARCH = 'search',
  RESULTS = 'results'
}

// SearchPage Component
interface SearchPageProps {
  fromAirport: string;
  setFromAirport: (value: string) => void;
  toAirport: string;
  setToAirport: (value: string) => void;
  departureTime: string;
  setDepartureTime: (value: string) => void;
  flightDuration: number | '';
  setFlightDuration: (value: number | '') => void;
  wantSunrise: boolean;
  setWantSunrise: (value: boolean) => void;
  wantSunset: boolean;
  setWantSunset: (value: boolean) => void;
  sunrisePreference: 'sunrise' | 'sunset';
  setSunrisePreference: (value: 'sunrise' | 'sunset') => void;
  loading: boolean;
  error: string | null;
  onSubmit: (e: React.FormEvent) => void;
  formatTimeDisplay: (date: Date) => string;
  getFlightEndTime: () => Date;
  getOptimalPeriodsInFlight: () => { sunrise: boolean; sunset: boolean; details: string[] };
}

const SearchPage: React.FC<SearchPageProps> = ({
  fromAirport, setFromAirport, toAirport, setToAirport, departureTime, setDepartureTime,
  flightDuration, setFlightDuration, wantSunrise, setWantSunrise, wantSunset, setWantSunset,
  sunrisePreference, setSunrisePreference, loading, error, onSubmit,
  formatTimeDisplay, getFlightEndTime, getOptimalPeriodsInFlight
}) => {
  return (
    <div className="search-page fade-in">
      <header className="app-header">
        <h1>✈️ SunSide - Perfect Window Seat Finder</h1>
        <p>Find the best window seat for sunrise and sunset views during your flight</p>
      </header>

      <main className="search-main">
        <div className="search-container">
          <div className="search-form card-elevated slide-up">
            <div className="form-header">
              <h2>🎯 Plan Your Journey</h2>
              <p>Enter your flight details to get personalized seat recommendations</p>
            </div>

            <div className="form-body">
              <form onSubmit={onSubmit}>
                {/* Left Column */}
                <div className="form-left-column">
                  <div className="form-section">
                    <h3 className="section-title">
                      <span className="section-icon">🛫</span>
                      Flight Details
                    </h3>

                    <div className="form-grid">
                      <div className="form-group">
                        <label className="input-label" htmlFor="fromAirport">From Airport</label>
                        <select
                          className="input-field"
                          id="fromAirport"
                          value={fromAirport}
                          onChange={(e) => setFromAirport(e.target.value)}
                          required
                        >
                          <option value="">Select departure</option>
                          <option value="DEL">🏛️ Delhi (DEL)</option>
                          <option value="JAI">🏰 Jaipur (JAI)</option>
                          <option value="BLR">🌆 Bangalore (BLR)</option>
                          <option value="BOM">🏙️ Mumbai (BOM)</option>
                          <option value="BHO">🌸 Bhopal (BHO)</option>
                          <option value="LKO">🕌 Lucknow (LKO)</option>
                        </select>
                      </div>

                      <div className="form-group">
                        <label className="input-label" htmlFor="toAirport">To Airport</label>
                        <select
                          className="input-field"
                          id="toAirport"
                          value={toAirport}
                          onChange={(e) => setToAirport(e.target.value)}
                          required
                        >
                          <option value="">Select destination</option>
                          <option value="DEL">🏛️ Delhi (DEL)</option>
                          <option value="JAI">🏰 Jaipur (JAI)</option>
                          <option value="BLR">🌆 Bangalore (BLR)</option>
                          <option value="BOM">🏙️ Mumbai (BOM)</option>
                          <option value="BHO">🌸 Bhopal (BHO)</option>
                          <option value="LKO">🕌 Lucknow (LKO)</option>
                        </select>
                      </div>
                    </div>

                    <div className="form-grid">
                      <div className="form-group">
                        <label className="input-label" htmlFor="departureTime">Departure Date & Time</label>
                        <input
                          className="input-field"
                          type="datetime-local"
                          id="departureTime"
                          value={departureTime}
                          onChange={(e) => setDepartureTime(e.target.value)}
                          required
                        />
                      </div>

                      <div className="form-group">
                        <label className="input-label" htmlFor="flightDuration">Flight Duration (minutes)</label>
                        <input
                          className="input-field"
                          type="number"
                          id="flightDuration"
                          min="30"
                          max="1440"
                          step="1"
                          value={flightDuration}
                          onChange={(e) => {
                            const value = e.target.value;
                            if (value === '') {
                              setFlightDuration('');
                            } else {
                              const intValue = parseInt(value, 10);
                              if (!isNaN(intValue)) {
                                setFlightDuration(intValue);
                              }
                            }
                          }}
                          placeholder="e.g., 120 (2 hours)"
                          required
                        />
                        {departureTime && flightDuration && (
                          <small className="duration-help" style={{ marginTop: '8px', display: 'block', color: 'var(--text-muted)', fontSize: '0.875rem' }}>
                            Flight time: {formatTimeDisplay(new Date(departureTime))} → {formatTimeDisplay(getFlightEndTime())} 
                            ({Math.floor(Number(flightDuration) / 60)}h {Number(flightDuration) % 60}m)
                          </small>
                        )}
                      </div>
                    </div>
                  </div>
                </div>

                {/* Right Column */}
                <div className="form-right-column">
                  <div className="form-section">
                    <div className="sun-preferences">
                      <h4>
                        <span className="section-icon">🌅</span>
                        Sun Viewing Preferences
                      </h4>
                      <div className="preference-note">
                        💡 Select what you'd like to see during your flight for optimal seat placement
                      </div>

                      <div className="checkbox-group">
                        <label className="checkbox-label">
                          <input
                            type="checkbox"
                            checked={wantSunrise}
                            onChange={(e) => setWantSunrise(e.target.checked)}
                          />
                          <span className="checkbox-text">🌅 I want to see the sunrise</span>
                        </label>

                        <label className="checkbox-label">
                          <input
                            type="checkbox"
                            checked={wantSunset}
                            onChange={(e) => setWantSunset(e.target.checked)}
                          />
                          <span className="checkbox-text">🌇 I want to see the sunset</span>
                        </label>
                      </div>

                      {wantSunrise && wantSunset && (
                        <div className="priority-selection">
                          <p className="priority-question">
                            🤔 If both are visible during your flight, which would you prefer?
                          </p>
                          <div className="radio-group">
                            <label className="radio-label">
                              <input
                                type="radio"
                                name="sunPreference"
                                value="sunrise"
                                checked={sunrisePreference === 'sunrise'}
                                onChange={(e) => setSunrisePreference(e.target.value as 'sunrise' | 'sunset')}
                              />
                              <span className="radio-text">🌅 Prioritize Sunrise</span>
                            </label>

                            <label className="radio-label">
                              <input
                                type="radio"
                                name="sunPreference"
                                value="sunset"
                                checked={sunrisePreference === 'sunset'}
                                onChange={(e) => setSunrisePreference(e.target.value as 'sunrise' | 'sunset')}
                              />
                              <span className="radio-text">🌇 Prioritize Sunset</span>
                            </label>
                          </div>
                        </div>
                      )}
                    </div>
                  </div>

                  {departureTime && flightDuration && (
                    <div className="form-section">
                      <div className="flight-timing-info bounce-in">
                        <h4>
                          <span className="section-icon">📅</span>
                          Flight Timing Analysis
                        </h4>
                        <div className="timing-details">
                          <p><strong>Departure:</strong> {formatTimeDisplay(new Date(departureTime))}</p>
                          <p><strong>Arrival:</strong> {formatTimeDisplay(getFlightEndTime())}</p>
                          <p><strong>Duration:</strong> {Math.floor(Number(flightDuration) / 60)}h {Number(flightDuration) % 60}m</p>

                          {(() => {
                            const optimalPeriods = getOptimalPeriodsInFlight();
                            return (
                              <div className="optimal-periods">
                                <p><strong>🎯 Available viewing opportunities:</strong></p>
                                {optimalPeriods.details.length > 0 ? (
                                  <ul>
                                    {optimalPeriods.details.map((detail, index) => (
                                      <li key={index}>{detail}</li>
                                    ))}
                                  </ul>
                                ) : (
                                  <p className="no-optimal">
                                    ⚠️ No optimal sunrise/sunset viewing times during this flight
                                  </p>
                                )}
                              </div>
                            );
                          })()}
                        </div>
                      </div>
                    </div>
                  )}

                  {/* Flight Tips Section - Always visible to fill space */}
                  <div className="form-section">
                    <div className="flight-tips-card">
                      <h4>
                        <span className="section-icon">💡</span>
                        Flight Seat Tips
                      </h4>
                      <div className="tips-content">
                        <div className="tip-item">
                          <span className="tip-icon">🌅</span>
                          <div className="tip-text">
                            <strong>Best Sunrise Views:</strong> East-facing windows during morning flights
                          </div>
                        </div>
                        <div className="tip-item">
                          <span className="tip-icon">🌇</span>
                          <div className="tip-text">
                            <strong>Best Sunset Views:</strong> West-facing windows during evening flights
                          </div>
                        </div>
                        <div className="tip-item">
                          <span className="tip-icon">🗺️</span>
                          <div className="tip-text">
                            <strong>Flight Direction:</strong> Our algorithm considers your exact route for precise recommendations
                          </div>
                        </div>
                        <div className="tip-item">
                          <span className="tip-icon">⏰</span>
                          <div className="tip-text">
                            <strong>Timing Matters:</strong> Sun position changes throughout your flight duration
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Submit Section - Full Width */}
                <div className="form-submit-section">
                  {error && (
                    <div className="error" style={{ marginBottom: 'var(--space-4)' }}>
                      <span className="error-icon">⚠️</span>
                      {error}
                    </div>
                  )}

                  <button 
                    type="submit" 
                    className="btn btn-primary btn-lg" 
                    disabled={loading}
                    style={{ width: '100%' }}
                  >
                    {loading ? (
                      <>
                        <div className="loading-spinner" style={{ width: '20px', height: '20px', border: '2px solid rgba(255,255,255,0.3)', borderTop: '2px solid white' }}></div>
                        Finding your perfect seat...
                      </>
                    ) : (
                      <>
                        🔍 Find My Perfect Seat
                      </>
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

// ResultsPage Component
interface ResultsPageProps {
  flightData: FlightData | null;
  fromAirport: string;
  toAirport: string;
  currentPosition: { lat: number; lon: number } | null;
  currentSunPosition: SunPosition | null;
  flightProgress: number;
  handleProgressChange: (newProgress: number) => void;
  handlePositionChange: (position: { lat: number; lon: number; sunPosition: SunPosition }) => void;
  isDragging: boolean;
  handleMouseDown: (e: React.MouseEvent) => void;
  handleClick: (e: React.MouseEvent) => void;
  onBack: () => void;
  departureTime: string;
  flightDuration: number | '';
}

// Helper function to get tooltip content based on sun position and time
const getSunTooltipContent = (sunPosition: SunPosition | null, flightProgress: number, startTime?: Date) => {
  if (!sunPosition || !startTime) return '';

  const currentTime = new Date(startTime.getTime() + (flightProgress / 100) * 3.5 * 60 * 60 * 1000); // Assuming ~3.5h flight
  const timeString = currentTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

  if (sunPosition.elevation < -6) {
    return `Night Time • ${timeString} • Sun ${Math.abs(sunPosition.elevation).toFixed(1)}° below horizon`;
  } else if (sunPosition.elevation < 0) {
    return `Civil Twilight • ${timeString} • Sun ${Math.abs(sunPosition.elevation).toFixed(1)}° below horizon`;
  } else if (sunPosition.elevation < 10) {
    return `Golden Hour • ${timeString} • Perfect for sunrise/sunset photos`;
  } else {
    return `Daylight • ${timeString} • Sun ${sunPosition.elevation.toFixed(1)}° above horizon`;
  }
};

