  }

  return null; // No hardcoded mapping found
};

// Function to calculate realistic sun azimuth based on time of day
const calculateRealisticSunAzimuth = (timeInMinutes: number, flightLonDiff: number): number => {
  // Convert 24-hour time to hour (with decimals)
  const hour = timeInMinutes / 60;

  // Realistic sun movement: East (90°) → South (180°) → West (270°)
  // But we need to account for the map coordinate conversion (+180°)
  let sunAzimuth: number;

  if (hour >= 6 && hour <= 18) {
    // Daytime: Sun moves in realistic arc from East to West
    const dayProgress = (hour - 6) / 12; // 0 to 1 (6 AM to 6 PM)

    // Realistic sun path: East → Southeast → South → Southwest → West
    // In map coordinates (compensated for +180° conversion):
    // East = 270°, South = 0°, West = 90°

    if (dayProgress <= 0.5) {
      // Morning: East (270°) → South (0°/360°)
      sunAzimuth = 270 + (dayProgress * 2) * 90; // 270° to 360°
      if (sunAzimuth >= 360) sunAzimuth -= 360; // Convert 360° to 0°
    } else {
      // Afternoon: South (0°) → West (90°)  
      const afternoonProgress = (dayProgress - 0.5) * 2; // 0 to 1
      sunAzimuth = 0 + afternoonProgress * 90; // 0° to 90°
    }

  } else if (hour > 18 || hour < 6) {
    // Night time: Moon follows opposite path
    if (hour > 18) {
      // Evening night (6 PM to midnight)
      const nightProgress = (hour - 18) / 6; // 0 to 1
      sunAzimuth = 90 + (nightProgress * 90); // 90° to 180° (West to South)
    } else {
      // Early morning night (midnight to 6 AM) 
      const nightProgress = hour / 6; // 0 to 1
      sunAzimuth = 180 + (nightProgress * 90); // 180° to 270° (South to East)
    }
  } else {
    // Default fallback
    sunAzimuth = 0; // South (compensated)
  }

  // The sunAzimuth is already calculated in map coordinates (compensated for +180° conversion)
  // Just ensure it's in valid range
  const relativeAzimuth = ((sunAzimuth % 360) + 360) % 360;

  return relativeAzimuth;
};

// Function to calculate bearing between two points
const calculateBearing = (lat1: number, lon1: number, lat2: number, lon2: number): number => {
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const lat1Rad = lat1 * Math.PI / 180;
  const lat2Rad = lat2 * Math.PI / 180;

  const y = Math.sin(dLon) * Math.cos(lat2Rad);
  const x = Math.cos(lat1Rad) * Math.sin(lat2Rad) - Math.sin(lat1Rad) * Math.cos(lat2Rad) * Math.cos(dLon);

  let bearing = Math.atan2(y, x) * 180 / Math.PI;
  bearing = (bearing + 360) % 360; // Normalize to 0-360

  // No adjustment needed for custom SVG airplane - it points north by default
  // The SVG is designed to point upward (0 degrees = north)

  return bearing;
};

