import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [fromAirport, setFromAirport] = useState('DEL');
  const [toAirport, setToAirport] = useState('JAI');
  const [departureTime, setDepartureTime] = useState('');
  const [routeData, setRouteData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const airports = [
    { code: 'DEL', name: 'Delhi (DEL)' },
    { code: 'JAI', name: 'Jaipur (JAI)' }
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!departureTime) {
      setError('Please select a departure time');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const response = await axios.get('/api/route', {
        params: {
          from: fromAirport,
          to: toAirport,
          depart: departureTime
        }
      });

      setRouteData(response.data);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch route data');
    } finally {
      setLoading(false);
    }
  };

  const getRecommendationText = (recommendation) => {
    switch (recommendation) {
      case 'left':
        return 'Window seat on the left side (A seats)';
      case 'right':
        return 'Window seat on the right side (F seats)';
      default:
        return recommendation;
    }
  };

  return (
    <div className="app">
      <div className="container">
        <div className="header">
          <h1>✈️ Flight Seat Recommender</h1>
          <p>Find the best window seat for sunrise and sunset views</p>
        </div>

        <div className="content">
          <form onSubmit={handleSubmit} className="form-section">
            <div className="form-group">
              <label htmlFor="fromAirport">From Airport</label>
              <select
                id="fromAirport"
                value={fromAirport}
                onChange={(e) => setFromAirport(e.target.value)}
              >
                {airports.map(airport => (
                  <option key={airport.code} value={airport.code}>
                    {airport.name}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="toAirport">To Airport</label>
              <select
                id="toAirport"
                value={toAirport}
                onChange={(e) => setToAirport(e.target.value)}
              >
                {airports.map(airport => (
                  <option key={airport.code} value={airport.code}>
                    {airport.name}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="departureTime">Departure Date & Time</label>
              <input
                type="datetime-local"
                id="departureTime"
                value={departureTime}
                onChange={(e) => setDepartureTime(e.target.value)}
                required
              />
            </div>

            <button type="submit" className="btn" disabled={loading}>
              {loading ? 'Finding your seat...' : 'Find My Seat'}
            </button>
          </form>

          {error && (
            <div className="error">
              {error}
            </div>
          )}

          {loading && (
            <div className="loading">
              <p>Calculating flight path and sun positions...</p>
            </div>
          )}

          {routeData && (
            <div className="results-section">
              <div className="recommendation">
                <h3>🎯 Seat Recommendation</h3>
                <p>{getRecommendationText(routeData.recommendation)}</p>
              </div>

              <div className="map-section">
                <h3>🗺️ Flight Path & Sun Positions</h3>
                <div style={{ padding: '20px', background: '#f8f9fa', borderRadius: '8px' }}>
                  <p><strong>Flight Path:</strong> {routeData.path.length} waypoints calculated</p>
                  <p><strong>Sun Positions:</strong> {routeData.sunPositions.length} positions analyzed</p>
                  <p><strong>Distance:</strong> ~{Math.round(routeData.path.length * 50)} km</p>
                  <p><strong>Flight Duration:</strong> ~{Math.round(routeData.path.length * 50 / 800 * 60)} minutes</p>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
