import React, { useState, useRef } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Popup, Polyline, Circle, useMap } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './App.css';

// Fix for default markers
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

// Custom airplane icon
const airplaneIcon = L.divIcon({
  html: '‚úàÔ∏è',
  className: 'airplane-icon',
  iconSize: [30, 30],
  iconAnchor: [15, 15]
});

// Custom sun icon
const sunIcon = L.divIcon({
  html: '‚òÄÔ∏è',
  className: 'sun-icon',
  iconSize: [40, 40],
  iconAnchor: [20, 20]
});

// Interactive Airplane Component
function InteractiveAirplane({ routeData, onPositionChange }) {
  const map = useMap();
  const [airplanePosition, setAirplanePosition] = useState(routeData.path[0]);
  const [sunPosition, setSunPosition] = useState(null);
  const [sunLine, setSunLine] = useState(null);

  const calculateSunPosition = (lat, lon, time) => {
    // Find the closest waypoint to calculate sun position
    const closestWaypoint = routeData.path.reduce((closest, waypoint) => {
      const distance = Math.sqrt(
        Math.pow(waypoint.lat - lat, 2) + Math.pow(waypoint.lon - lon, 2)
      );
      return distance < closest.distance ? { waypoint, distance } : closest;
    }, { waypoint: routeData.path[0], distance: Infinity });

    const sunData = routeData.sunPositions.find(
      sun => sun.lat === closestWaypoint.waypoint.lat && sun.lon === closestWaypoint.waypoint.lon
    );

    if (sunData) {
      // Calculate sun position based on azimuth
      const azimuth = sunData.azimuth * Math.PI / 180;
      const distance = 0.5; // Distance from airplane to sun marker
      
      const sunLat = lat + distance * Math.cos(azimuth);
      const sunLon = lon + distance * Math.sin(azimuth);
      
      return { lat: sunLat, lon: sunLon, elevation: sunData.elevation, azimuth: sunData.azimuth };
    }
    return null;
  };

  const handleAirplaneDrag = (e) => {
    const newPosition = e.target.getLatLng();
    setAirplanePosition({ lat: newPosition.lat, lon: newPosition.lng });
    
    const sunPos = calculateSunPosition(newPosition.lat, newPosition.lng);
    setSunPosition(sunPos);
    
    if (onPositionChange) {
      onPositionChange(newPosition, sunPos);
    }
  };

  React.useEffect(() => {
    if (routeData && routeData.path.length > 0) {
      setAirplanePosition(routeData.path[0]);
      const initialSun = calculateSunPosition(routeData.path[0].lat, routeData.path[0].lon);
      setSunPosition(initialSun);
    }
  }, [routeData]);

  if (!routeData || !airplanePosition) return null;

  return (
    <>
      <Marker
        position={[airplanePosition.lat, airplanePosition.lon]}
        icon={airplaneIcon}
        draggable={true}
        eventHandlers={{
          dragend: handleAirplaneDrag
        }}
      >
        <Popup>
          <div>
            <h4>‚úàÔ∏è Your Flight</h4>
            <p>Drag to move along the route</p>
            <p><strong>Position:</strong> {airplanePosition.lat.toFixed(4)}, {airplanePosition.lon.toFixed(4)}</p>
          </div>
        </Popup>
      </Marker>

      {sunPosition && (
        <>
          <Marker
            position={[sunPosition.lat, sunPosition.lon]}
            icon={sunIcon}
          >
            <Popup>
              <div>
                <h4>‚òÄÔ∏è Sun Position</h4>
                <p><strong>Elevation:</strong> {sunPosition.elevation.toFixed(1)}¬∞</p>
                <p><strong>Azimuth:</strong> {sunPosition.azimuth.toFixed(1)}¬∞</p>
                <p><strong>Direction:</strong> {sunPosition.azimuth < 90 || sunPosition.azimuth > 270 ? 'Left side' : 'Right side'}</p>
              </div>
            </Popup>
          </Marker>

          <Polyline
            positions={[
              [airplanePosition.lat, airplanePosition.lon],
              [sunPosition.lat, sunPosition.lon]
            ]}
            color="#ffd700"
            weight={3}
            opacity={0.8}
            dashArray="10, 5"
          />
        </>
      )}
    </>
  );
}

function App() {
  const [fromAirport, setFromAirport] = useState('DEL');
  const [toAirport, setToAirport] = useState('JAI');
  const [departureTime, setDepartureTime] = useState('');
  const [routeData, setRouteData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [currentPosition, setCurrentPosition] = useState(null);
  const [currentSunPosition, setCurrentSunPosition] = useState(null);

  const airports = [
    { code: 'DEL', name: 'Delhi (DEL)', coords: [28.5562, 77.1000] },
    { code: 'JAI', name: 'Jaipur (JAI)', coords: [26.8282, 75.8056] }
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!departureTime) {
      setError('Please select a departure time');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const response = await axios.get('/api/route', {
        params: {
          from: fromAirport,
          to: toAirport,
          depart: departureTime
        }
      });

      setRouteData(response.data);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch route data');
    } finally {
      setLoading(false);
    }
  };

  const getRecommendationText = (recommendation) => {
    switch (recommendation) {
      case 'left':
        return 'Window seat on the left side (A seats)';
      case 'right':
        return 'Window seat on the right side (F seats)';
      default:
        return recommendation;
    }
  };

  const getFromAirport = () => airports.find(a => a.code === fromAirport);
  const getToAirport = () => airports.find(a => a.code === toAirport);

  const handlePositionChange = (position, sunPosition) => {
    setCurrentPosition(position);
    setCurrentSunPosition(sunPosition);
  };

  return (
    <div className="app">
      <div className="container">
        <div className="header">
          <h1>‚úàÔ∏è Flight Seat Recommender</h1>
          <p>Find the best window seat for sunrise and sunset views</p>
        </div>

        <div className="content">
          <form onSubmit={handleSubmit} className="form-section">
            <div className="form-group">
              <label htmlFor="fromAirport">From Airport</label>
              <select
                id="fromAirport"
                value={fromAirport}
                onChange={(e) => setFromAirport(e.target.value)}
              >
                {airports.map(airport => (
                  <option key={airport.code} value={airport.code}>
                    {airport.name}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="toAirport">To Airport</label>
              <select
                id="toAirport"
                value={toAirport}
                onChange={(e) => setToAirport(e.target.value)}
              >
                {airports.map(airport => (
                  <option key={airport.code} value={airport.code}>
                    {airport.name}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="departureTime">Departure Date & Time</label>
              <input
                type="datetime-local"
                id="departureTime"
                value={departureTime}
                onChange={(e) => setDepartureTime(e.target.value)}
                required
              />
            </div>

            <button type="submit" className="btn" disabled={loading}>
              {loading ? 'Finding your seat...' : 'Find My Seat'}
            </button>
          </form>

          {error && (
            <div className="error">
              {error}
            </div>
          )}

          {loading && (
            <div className="loading">
              <p>Calculating flight path and sun positions...</p>
            </div>
          )}

          {routeData && (
            <div className="results-container">
              <div className="map-section">
                <h3>üó∫Ô∏è Interactive Flight Route</h3>
                <p className="map-instructions">Drag the airplane ‚úàÔ∏è along the route to see sun position changes</p>
                <div className="map-container">
                  <MapContainer
                    center={[27.5, 76.5]}
                    zoom={7}
                    style={{ height: '500px', width: '100%' }}
                  >
                    <TileLayer
                      url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                      attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    />

                    {/* Flight path */}
                    <Polyline
                      positions={routeData.path.map(point => [point.lat, point.lon])}
                      color="#667eea"
                      weight={3}
                      opacity={0.8}
                    >
                      <Popup>
                        <div>
                          <h4>Flight Path</h4>
                          <p>Great circle route between airports</p>
                        </div>
                      </Popup>
                    </Polyline>

                    {/* Airport markers */}
                    <Marker position={getFromAirport().coords}>
                      <Popup>
                        <div>
                          <h4>Departure: {getFromAirport().name}</h4>
                          <p>Starting point of your journey</p>
                        </div>
                      </Popup>
                    </Marker>

                    <Marker position={getToAirport().coords}>
                      <Popup>
                        <div>
                          <h4>Arrival: {getToAirport().name}</h4>
                          <p>Destination of your journey</p>
                        </div>
                      </Popup>
                    </Marker>

                    {/* Interactive Airplane */}
                    <InteractiveAirplane 
                      routeData={routeData} 
                      onPositionChange={handlePositionChange}
                    />
                  </MapContainer>
                </div>
              </div>

              <div className="results-section">
                <div className="recommendation">
                  <h3>üéØ Seat Recommendation</h3>
                  <p>{getRecommendationText(routeData.recommendation)}</p>
                </div>

                {currentPosition && currentSunPosition && (
                  <div className="live-position">
                    <h3>üìç Live Position</h3>
                    <div className="position-details">
                      <p><strong>Airplane:</strong> {currentPosition.lat.toFixed(4)}, {currentPosition.lon.toFixed(4)}</p>
                      <p><strong>Sun Elevation:</strong> {currentSunPosition.elevation.toFixed(1)}¬∞</p>
                      <p><strong>Sun Direction:</strong> {currentSunPosition.azimuth.toFixed(1)}¬∞</p>
                      <p><strong>Best Seat:</strong> {currentSunPosition.azimuth < 90 || currentSunPosition.azimuth > 270 ? 'Left window (A seats)' : 'Right window (F seats)'}</p>
                    </div>
                  </div>
                )}

                <div className="flight-details">
                  <h3>üìä Flight Details</h3>
                  <div className="details-grid">
                    <div className="detail-item">
                      <strong>Flight Path:</strong> {routeData.path.length} waypoints calculated
                    </div>
                    <div className="detail-item">
                      <strong>Sun Positions:</strong> {routeData.sunPositions.length} positions analyzed
                    </div>
                    <div className="detail-item">
                      <strong>Distance:</strong> ~{Math.round(routeData.path.length * 50)} km
                    </div>
                    <div className="detail-item">
                      <strong>Flight Duration:</strong> ~{Math.round(routeData.path.length * 50 / 800 * 60)} minutes
                    </div>
                    <div className="detail-item">
                      <strong>Route:</strong> {getFromAirport().name} ‚Üí {getToAirport().name}
                    </div>
                    <div className="detail-item">
                      <strong>Departure:</strong> {new Date(departureTime).toLocaleString()}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
