import React, { useEffect, useRef, useState } from 'react';
import * as THREE from 'three';

// Seat positions mapping (relative to airplane interior)
const SEAT_POSITIONS: { [key: string]: { position: [number, number, number], target: [number, number, number] } } = {
  'A1': { position: [-2, 1, 5], target: [0, 0, 0] },
  'B1': { position: [-1, 1, 5], target: [0, 0, 0] },
  'C1': { position: [1, 1, 5], target: [0, 0, 0] },
  'D1': { position: [2, 1, 5], target: [0, 0, 0] },
  'A2': { position: [-2, 1, 3], target: [0, 0, 0] },
  'B2': { position: [-1, 1, 3], target: [0, 0, 0] },
  'C2': { position: [1, 1, 3], target: [0, 0, 0] },
  'D2': { position: [2, 1, 3], target: [0, 0, 0] },
  'A3': { position: [-2, 1, 1], target: [0, 0, 0] },
  'B3': { position: [-1, 1, 1], target: [0, 0, 0] },
  'C3': { position: [1, 1, 1], target: [0, 0, 0] },
  'D3': { position: [2, 1, 1], target: [0, 0, 0] },
  'A4': { position: [-2, 1, -1], target: [0, 0, 0] },
  'B4': { position: [-1, 1, -1], target: [0, 0, 0] },
  'C4': { position: [1, 1, -1], target: [0, 0, 0] },
  'D4': { position: [2, 1, -1], target: [0, 0, 0] },
  'A5': { position: [-2, 1, -3], target: [0, 0, 0] },
  'B5': { position: [-1, 1, -3], target: [0, 0, 0] },
  'C5': { position: [1, 1, -3], target: [0, 0, 0] },
  'D5': { position: [2, 1, -3], target: [0, 0, 0] },
};

interface AirplaneExperienceProps {
  selectedSeat?: string;
  onSeatChange?: (seat: string) => void;
}

const AirplaneExperience: React.FC<AirplaneExperienceProps> = ({ selectedSeat, onSeatChange }) => {
  const mountRef = useRef<HTMLDivElement>(null);
  const sceneRef = useRef<THREE.Scene>();
  const rendererRef = useRef<THREE.WebGLRenderer>();
  const cameraRef = useRef<THREE.PerspectiveCamera>();
  const controlsRef = useRef<any>();
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (!mountRef.current) return;

    // Scene setup
    const scene = new THREE.Scene();
    sceneRef.current = scene;

    // Camera setup
    const camera = new THREE.PerspectiveCamera(
      75,
      mountRef.current.clientWidth / mountRef.current.clientHeight,
      0.1,
      1000
    );
    camera.position.set(0, 1.5, 5);
    cameraRef.current = camera;

    // Renderer setup
    const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
    renderer.setSize(mountRef.current.clientWidth, mountRef.current.clientHeight);
    renderer.setClearColor(0x87CEEB, 1); // Sky blue background
    renderer.shadowMap.enabled = true;
    renderer.shadowMap.type = THREE.PCFSoftShadowMap;
    rendererRef.current = renderer;

    mountRef.current.appendChild(renderer.domElement);

    // Lighting setup
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
    scene.add(ambientLight);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
    directionalLight.position.set(5, 10, 5);
    directionalLight.castShadow = true;
    directionalLight.shadow.mapSize.width = 2048;
    directionalLight.shadow.mapSize.height = 2048;
    scene.add(directionalLight);

    // Create airplane interior (simplified geometry)
    createAirplaneInterior(scene);

    // Create skybox
    createSkybox(scene);

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);
      
      if (controlsRef.current) {
        controlsRef.current.update();
      }
      
      renderer.render(scene, camera);
    };

    // Simulate loading completion
    setTimeout(() => {
      setIsLoading(false);
    }, 1000);

    animate();

    // Handle window resize
    const handleResize = () => {
      if (mountRef.current && camera && renderer) {
        camera.aspect = mountRef.current.clientWidth / mountRef.current.clientHeight;
        camera.updateProjectionMatrix();
        renderer.setSize(mountRef.current.clientWidth, mountRef.current.clientHeight);
      }
    };

    window.addEventListener('resize', handleResize);

    // Setup basic orbit controls (simplified version)
    setupSimpleControls(camera, renderer.domElement);

    return () => {
      window.removeEventListener('resize', handleResize);
      if (mountRef.current && renderer.domElement) {
        mountRef.current.removeChild(renderer.domElement);
      }
      renderer.dispose();
    };
  }, []);

  // Update camera position when seat changes
  useEffect(() => {
    if (selectedSeat && SEAT_POSITIONS[selectedSeat] && cameraRef.current) {
      const seatData = SEAT_POSITIONS[selectedSeat];
      const camera = cameraRef.current;
      
      // Smooth camera transition
      const startPos = camera.position.clone();
      const endPos = new THREE.Vector3(...seatData.position);
      const duration = 1000; // 1 second
      const startTime = Date.now();
      
      const animateCamera = () => {
        const elapsed = Date.now() - startTime;
        const progress = Math.min(elapsed / duration, 1);
        
        // Easing function
        const easeProgress = 1 - Math.pow(1 - progress, 3);
        
        camera.position.lerpVectors(startPos, endPos, easeProgress);
        camera.lookAt(...seatData.target);
        
        if (progress < 1) {
          requestAnimationFrame(animateCamera);
        }
      };
      
      animateCamera();
    }
  }, [selectedSeat]);

  const createAirplaneInterior = (scene: THREE.Scene) => {
    // Fuselage (main cabin)
    const fuselageGeometry = new THREE.CylinderGeometry(2.5, 2.5, 12, 32);
    const fuselageMaterial = new THREE.MeshLambertMaterial({ 
      color: 0xf0f0f0,
      side: THREE.BackSide 
    });
    const fuselage = new THREE.Mesh(fuselageGeometry, fuselageMaterial);
    fuselage.rotation.z = Math.PI / 2;
    scene.add(fuselage);

    // Floor
    const floorGeometry = new THREE.PlaneGeometry(12, 4);
    const floorMaterial = new THREE.MeshLambertMaterial({ color: 0x2c3e50 });
    const floor = new THREE.Mesh(floorGeometry, floorMaterial);
    floor.rotation.x = -Math.PI / 2;
    floor.position.y = -1.2;
    floor.receiveShadow = true;
    scene.add(floor);

    // Create seats
    createSeats(scene);

    // Windows
    createWindows(scene);

    // Overhead compartments
    createOverheadCompartments(scene);
  };

  const createSeats = (scene: THREE.Scene) => {
    const seatGeometry = new THREE.BoxGeometry(0.4, 0.8, 0.4);
    const seatMaterial = new THREE.MeshLambertMaterial({ color: 0x1e3a8a });

    Object.entries(SEAT_POSITIONS).forEach(([seatId, seatData]) => {
      const seat = new THREE.Mesh(seatGeometry, seatMaterial);
      seat.position.set(...seatData.position);
      seat.position.y = 0;
      seat.castShadow = true;
      seat.userData = { seatId };
      scene.add(seat);

      // Seat label
      const canvas = document.createElement('canvas');
      const context = canvas.getContext('2d')!;
      canvas.width = 128;
      canvas.height = 64;
      context.fillStyle = '#ffffff';
      context.fillRect(0, 0, 128, 64);
      context.fillStyle = '#000000';
      context.font = '24px Arial';
      context.textAlign = 'center';
      context.fillText(seatId, 64, 40);

      const texture = new THREE.CanvasTexture(canvas);
      const labelMaterial = new THREE.MeshBasicMaterial({ map: texture });
      const labelGeometry = new THREE.PlaneGeometry(0.5, 0.25);
      const label = new THREE.Mesh(labelGeometry, labelMaterial);
      label.position.set(...seatData.position);
      label.position.y = 1;
      label.lookAt(0, 1, 0);
      scene.add(label);
    });
  };

  const createWindows = (scene: THREE.Scene) => {
    const windowGeometry = new THREE.PlaneGeometry(0.6, 0.4);
    const windowMaterial = new THREE.MeshBasicMaterial({ 
      color: 0x87CEEB, 
      transparent: true, 
      opacity: 0.7 
    });

    // Left side windows
    for (let i = 0; i < 6; i++) {
      const window = new THREE.Mesh(windowGeometry, windowMaterial);
      window.position.set(-2.4, 0.5, 4 - i * 1.5);
      window.rotation.y = Math.PI / 2;
      scene.add(window);
    }

    // Right side windows
    for (let i = 0; i < 6; i++) {
      const window = new THREE.Mesh(windowGeometry, windowMaterial);
      window.position.set(2.4, 0.5, 4 - i * 1.5);
      window.rotation.y = -Math.PI / 2;
      scene.add(window);
    }
  };

  const createOverheadCompartments = (scene: THREE.Scene) => {
    const compartmentGeometry = new THREE.BoxGeometry(4, 0.6, 0.8);
    const compartmentMaterial = new THREE.MeshLambertMaterial({ color: 0xcccccc });

    for (let i = 0; i < 5; i++) {
      const compartment = new THREE.Mesh(compartmentGeometry, compartmentMaterial);
      compartment.position.set(0, 2, 4 - i * 2);
      scene.add(compartment);
    }
  };

  const createSkybox = (scene: THREE.Scene) => {
    // Create a simple gradient skybox
    const skyGeometry = new THREE.SphereGeometry(500, 32, 32);
    const skyMaterial = new THREE.MeshBasicMaterial({ 
      color: 0x87CEEB, 
      side: THREE.BackSide 
    });
    const skybox = new THREE.Mesh(skyGeometry, skyMaterial);
    scene.add(skybox);

    // Add some clouds
    const cloudGeometry = new THREE.SphereGeometry(20, 16, 16);
    const cloudMaterial = new THREE.MeshBasicMaterial({ 
      color: 0xffffff, 
      transparent: true, 
      opacity: 0.8 
    });

    for (let i = 0; i < 10; i++) {
      const cloud = new THREE.Mesh(cloudGeometry, cloudMaterial);
      cloud.position.set(
        (Math.random() - 0.5) * 800,
        Math.random() * 100 + 50,
        (Math.random() - 0.5) * 800
      );
      cloud.scale.setScalar(Math.random() * 2 + 0.5);
      scene.add(cloud);
    }
  };

  const setupSimpleControls = (camera: THREE.Camera, domElement: HTMLElement) => {
    let isMouseDown = false;
    let mouseX = 0;
    let mouseY = 0;
    let cameraDistance = 5;

    const onMouseDown = (event: MouseEvent) => {
      isMouseDown = true;
      mouseX = event.clientX;
      mouseY = event.clientY;
    };

    const onMouseMove = (event: MouseEvent) => {
      if (!isMouseDown) return;

      const deltaX = event.clientX - mouseX;
      const deltaY = event.clientY - mouseY;

      // Rotate camera around target
      const spherical = new THREE.Spherical();
      spherical.setFromVector3(camera.position);
      spherical.theta -= deltaX * 0.01;
      spherical.phi += deltaY * 0.01;
      spherical.phi = Math.max(0.1, Math.min(Math.PI - 0.1, spherical.phi));

      camera.position.setFromSpherical(spherical);
      camera.lookAt(0, 0, 0);

      mouseX = event.clientX;
      mouseY = event.clientY;
    };

    const onMouseUp = () => {
      isMouseDown = false;
    };

    const onWheel = (event: WheelEvent) => {
      cameraDistance += event.deltaY * 0.01;
      cameraDistance = Math.max(2, Math.min(15, cameraDistance));
      
      const direction = camera.position.clone().normalize();
      camera.position.copy(direction.multiplyScalar(cameraDistance));
    };

    domElement.addEventListener('mousedown', onMouseDown);
    domElement.addEventListener('mousemove', onMouseMove);
    domElement.addEventListener('mouseup', onMouseUp);
    domElement.addEventListener('wheel', onWheel);

    controlsRef.current = {
      update: () => {} // Placeholder for compatibility
    };
  };

  const handleSeatSelection = (seat: string) => {
    if (onSeatChange) {
      onSeatChange(seat);
    }
  };

  return (
    <div style={{ width: '100%', height: '100vh', position: 'relative' }}>
      {/* Loading overlay */}
      {isLoading && (
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0, 0, 0, 0.8)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000,
          color: 'white',
          fontSize: '18px'
        }}>
          <div style={{ textAlign: 'center' }}>
            <div style={{ marginBottom: '20px', fontSize: '24px' }}>‚úàÔ∏è</div>
            <div>Loading 3D Airplane Experience...</div>
          </div>
        </div>
      )}

      {/* Error message */}
      {error && (
        <div style={{
          position: 'absolute',
          top: 20,
          left: 20,
          right: 20,
          background: 'rgba(255, 0, 0, 0.9)',
          color: 'white',
          padding: '15px',
          borderRadius: '8px',
          zIndex: 1000
        }}>
          Error: {error}
        </div>
      )}

      {/* Seat selection UI */}
      <div style={{
        position: 'absolute',
        top: 20,
        right: 20,
        background: 'rgba(255, 255, 255, 0.95)',
        padding: '20px',
        borderRadius: '12px',
        boxShadow: '0 4px 12px rgba(0, 0, 0, 0.3)',
        zIndex: 100,
        minWidth: '200px'
      }}>
        <h3 style={{ margin: '0 0 15px 0', color: '#2c3e50' }}>ü™ë Select Your Seat</h3>
        <select
          value={selectedSeat || ''}
          onChange={(e) => handleSeatSelection(e.target.value)}
          style={{
            width: '100%',
            padding: '10px',
            borderRadius: '6px',
            border: '2px solid #3498db',
            fontSize: '16px',
            background: 'white'
          }}
        >
          <option value="">Choose a seat...</option>
          {Object.keys(SEAT_POSITIONS).map(seatId => (
            <option key={seatId} value={seatId}>
              Seat {seatId}
            </option>
          ))}
        </select>
        
        {selectedSeat && (
          <div style={{
            marginTop: '15px',
            padding: '10px',
            background: '#e8f5e8',
            borderRadius: '6px',
            color: '#2c5530'
          }}>
            <strong>Current View:</strong> Seat {selectedSeat}
          </div>
        )}
      </div>

      {/* Controls info */}
      <div style={{
        position: 'absolute',
        bottom: 20,
        left: 20,
        background: 'rgba(0, 0, 0, 0.7)',
        color: 'white',
        padding: '15px',
        borderRadius: '8px',
        fontSize: '14px'
      }}>
        <div><strong>üñ±Ô∏è Controls:</strong></div>
        <div>‚Ä¢ Drag to rotate view</div>
        <div>‚Ä¢ Scroll to zoom in/out</div>
        <div>‚Ä¢ Select seat to move camera</div>
      </div>

      {/* Three.js mount point */}
      <div 
        ref={mountRef} 
        style={{ 
          width: '100%', 
          height: '100%',
          cursor: 'grab'
        }}
      />
    </div>
  );
};

export default AirplaneExperience;
